    private void jRadioButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jRadioButton1ActionPerformed
 
     
        try {

            int numero1, numero2;
            numero1 = Integer.parseInt(this.txtvalor1.getText());
            numero2 = Integer.parseInt(this.txtvalor2.getText());

            int resultado = numero1 + numero2;
            txtresultado.setText(String.valueOf(resultado));

        } catch (NumberFormatException ex) {
           JOptionPane.showMessageDialog(this, " no se puede realizar la operaci贸n, "
                    + " puede que no halla ingresado valores "
                    + "\n o los valores ingresados no son validos", "ERROR", JOptionPane.ERROR_MESSAGE);
        }
        // TODO add your handling code here:
    }//GEN-LAST:event_jRadioButton1ActionPerformed

